#include <string>
#include <iostream>
#include <unordered_map>
using namespace std;

class Solution {
public:
    bool ok_counter(unordered_map<char, int> counter, int k){
        for(int i=0;i<26;i++){
            if(counter['a' + i] != 0 && counter['a' + i] < k)
                return false;
        }
        return true;
    }
    int longestSubstringV2(string s, int k){
        if(s.size()==0)
            return 0;
        if(k == 0)
            return s.length();
        unordered_map<char, int> counter;
        for(auto c: s){
            counter[c] = counter[c] + 1;
        }
        int l = 0;
        int r = s.length()-1;
        while(l <= r){
            while(l <= r && counter[s[l]] < k){
                counter[s[l]] -= 1;
                l += 1;

            }
            while(l <= r && counter[s[r]] < k){
                counter[s[r]] -= 1;
                r -= 1;
            }
            if(ok_counter(counter, k))
                return (r - l + 1);
            int ll = l;
            int rr = r;
            while(l <= r){
                if(l <= r && counter[s[l]] < k){
                    r = rr;
                    while(ll < l){
                        counter[s[ll]] -= 1;
                        ll += 1;
                    }
                    break;
                }
                if(l <= r && counter[s[r]] < k){
                    l = ll;
                    while(rr > r){
                        counter[s[rr]] -= 1;
                        rr -= 1;
                    }
                    break;
                }
                l += 1;
                r -= 1;
            }
        }
        return 0;
    }
    int longestSubstring(string s, int k) {
        int len = s.length();
        if(len == 0)
            return 0;
        int result = 0;
        for(int i=0;i<len;i++){
            int dp[len][26];
            memset(dp, 0, sizeof(dp));
            for(int j=i;j<len;j++){
                if(j != 0){
                    for(int z=0;z<26;z++){
                        dp[j][z] = dp[j-1][z];
                    }
                }
                if((j-i+1) < k){
                    continue;
                }
                int c = int(s[j] - 'a');
                dp[j][c] += 1;
                bool flag = true;
                for(int z=0;z<26;z++){
                    if(dp[j][z] == 0 || dp[j][z] >= k){
                        continue;
                    }else{
                        flag = false;
                        break;
                    }
                }
                if(flag){
                    result = max(result, (j-i+1));
                }
            }
        }
        return result;
    }
    static void solution(){
        // string s = "";
        string s = "bbaaacbd";
        int k = 3;
        Solution solution1;
        cout<<solution1.longestSubstringV2(s, k)<<endl;
        // "ababacb"
        // 3
    }
};